import React, { createContext, useContext, useState, useEffect } from 'react';
import { authAPI } from '../services/api';

const AuthContext = createContext();

// Helper function to extract error message from FastAPI error response
const extractErrorMessage = (error) => {
  if (!error) return 'An error occurred';

  // If error is a string, return it
  if (typeof error === 'string') return error;

  // If error is an object with a detail property
  if (error.detail) {
    // If detail is an array (FastAPI validation errors)
    if (Array.isArray(error.detail)) {
      // Extract messages from validation errors
      return error.detail
        .map(err => {
          const field = err.loc ? err.loc.slice(1).join('.') : 'field';
          return `${field}: ${err.msg}`;
        })
        .join(', ');
    }
    // If detail is a string
    if (typeof error.detail === 'string') {
      return error.detail;
    }
  }

  // If error has a message property
  if (error.message) return error.message;

  // Default fallback
  return 'An error occurred';
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      setUser(JSON.parse(userData));
    }
    setLoading(false);
  }, []);

  const login = async (credentials) => {
    try {
      const response = await authAPI.login(credentials);
      const { access_token, user_type, user_id, recruiter_id } = response.data;

      const userData = {
        user_type,
        id: user_id || recruiter_id,
        email: credentials.email
      };

      localStorage.setItem('token', access_token);
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);

      return { success: true, data: response.data };
    } catch (error) {
      const errorMessage = error.response?.data 
        ? extractErrorMessage(error.response.data)
        : error.message || 'Login failed';
      return { 
        success: false, 
        error: errorMessage
      };
    }
  };

  const register = async (userData, type) => {
    try {
      const response = type === 'user' 
        ? await authAPI.registerUser(userData)
        : await authAPI.registerRecruiter(userData);

      return { success: true, data: response.data };
    } catch (error) {
      const errorMessage = error.response?.data 
        ? extractErrorMessage(error.response.data)
        : error.message || 'Registration failed';
      return { 
        success: false, 
        error: errorMessage
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  const value = {
    user,
    login,
    register,
    logout,
    loading,
    isAuthenticated: !!user,
    isUser: user?.user_type === 'user',
    isRecruiter: user?.user_type === 'recruiter'
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
