                    <div className="mt-2 text-sm text-gray-500">
                      {formData.description.length}/2000 characters
                    </div>
                  </div>
                </div>
              </div>

              {/* Job Details Section */}
              <div className="bg-gradient-to-r from-green-50/50 to-emerald-50/50 rounded-2xl p-6 border border-green-100">
