                    <label htmlFor="location" className="flex items-center text-sm font-semibold text-gray-700 mb-2">
                      <svg className="w-4 h-4 mr-2 text-green-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                      Location
                    </label>
                    <input
                      type="text"
                      name="location"
                      id="location"
                      value={formData.location}
                      onChange={handleChange}
                      className="block w-full border-2 border-gray-200 rounded-2xl px-4 py-4 focus:outline-none focus:ring-2 focus:ring-green-500/20 focus:border-green-500 transition-all duration-200 text-lg placeholder-gray-400"
                      placeholder="e.g., San Francisco, CA or Remote"
                    />
                  </div>

                  {/* Salary */}
                  <div>
                    <label htmlFor="salary" className="flex items-center text-sm font-semibold text-gray-700 mb-2">
                      <svg className="w-4 h-4 mr-2 text-yellow-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
                      </svg>
                      Salary Range
                    </label>
                    <input
                      type="text"
                      name="salary"
                      id="salary"
                      value={formData.salary}
                      onChange={handleChange}
                      className="block w-full border-2 border-gray-200 rounded-2xl px-4 py-4 focus:outline-none focus:ring-2 focus:ring-yellow-500/20 focus:border-yellow-500 transition-all duration-200 text-lg placeholder-gray-400"
                      placeholder="e.g., $80,000 - $120,000"
                    />
                  </div>

                  {/* Industry */}
                  <div className="md:col-span-2">
                    <label htmlFor="industry" className="flex items-center text-sm font-semibold text-gray-700 mb-2">
                      <svg className="w-4 h-4 mr-2 text-indigo-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                      </svg>
                      Industry
                    </label>
                    <select
                      name="industry"
                      id="industry"
                      value={formData.industry}
                      onChange={handleChange}
                      className="block w-full border-2 border-gray-200 rounded-2xl px-4 py-4 focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 transition-all duration-200 text-lg bg-white"
                    >
                      <option value="">Select an industry</option>
                      <option value="Technology"> Technology</option>
                      <option value="Healthcare"> Healthcare</option>
                      <option value="Finance"> Finance</option>
                      <option value="Education"> Education</option>
                      <option value="Marketing"> Marketing</option>
                      <option value="Manufacturing"> Manufacturing</option>
                      <option value="Retail"> Retail</option>
                      <option value="Consulting"> Consulting</option>
                      <option value="Other"> Other</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Skills Section */}
              <div className="bg-gradient-to-r from-purple-50/50 to-pink-50/50 rounded-2xl p-6 border border-purple-100">
                <div className="flex items-center mb-6">
                  <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-pink-600 rounded-lg flex items-center justify-center mr-3">
                    <svg className="w-5 h-5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                    </svg>
                  </div>
                  <h3 className="text-xl font-bold text-gray-900"> Required Skills</h3>
                </div>

                <div className="space-y-6">
                  {/* Added Skills */}
                  {formData.skills_required.length > 0 && (
                    <div>
                      <h4 className="text-sm font-semibold text-gray-700 mb-3">Added Skills:</h4>
                      <div className="flex flex-wrap gap-3">
                        {formData.skills_required.map((skill, index) => (
                          <span
                            key={index}
                            className="group inline-flex items-center px-4 py-2 rounded-xl text-sm font-semibold bg-gradient-to-r from-purple-100 to-pink-100 text-purple-800 border border-purple-200 shadow-lg hover:shadow-xl transition-all duration-200"
